import React from 'react';
import { Component } from 'react';
import './App.css';
import Navigation from './components/Navigation/Navigation';
import Logo from './components/Logo/Logo';
import FaceRecognition from './components/FaceRecognition/FaceRecognition';
import Rank from './components/Rank/Rank';
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm';
import Particles from 'react-particles-js';
import Clarifai from 'clarifai';

const app = new Clarifai.App({
  apiKey: 'fc0d3c88b1464cdfb2e6b5ef02f6e320'
 });


const particlesOptions = {
  polygon: {
    enable: true,
    type: 'inside',
    move: {
      radius: 10
    },
    url: 'path/to/svg.svg'
  }

}
class App extends Component {
  constructor() {
    super();
    this.state = {
      input: '',
      imageUrl: '',
      box: {}, 
    }
  }

  calculateFaceLocation = (data) => {
    const clarifaiFace = data.response.outputs[0].data.regions[0].region_info.bounding_box;
    const image = document.getElementById('inputimage');
    const width = Number(image.width);
    const height = Number(image.height);
    console.log(width, height);

  }


  onInputChange = (event) => {
    this.setState({input: event.target.value});

  }

  onButtonSubmit = () => {
    this.setState({imageUrl: this.state.intput})
    app.models.predict(Clarifai.FACE_DETECT_MODEL, this.state.input)
    .then(response =>  this.calculateFaceLocation(response))
    .catch(err => console.log(err))
  }

  render() {

    const { imageUrl } = this.state;

    return (
      <div className="App">
        <Particles className='particles'
          params={{ particlesOptions }} />
        <Navigation />
        <Logo />
        <Rank />
        <ImageLinkForm onInputChange={this.onInputChange} onButtonSubmit={this.onButtonSubmit}/>
        {/* here is where i update the state immediately and this is causing state issues  */}
        <FaceRecognition imageUrl={imageUrl}/> 


      </div>
    );
  }
}


export default App;
